import React, { useState } from 'react';

const Registration= () => {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [company, setCompany] = useState('');
    const [submittedData, setSubmittedData] = useState(null);

    const handleNameChange = (e) => setName(e.target.value);
    const handleAgeChange = (e) => setAge(e.target.value);
    const handleCompanyChange = (e) => setCompany(e.target.value);

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = { name, age, company };
        setSubmittedData(data);
        
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div></div>
               
                <input type="number" placeholder="Age" value={age} onChange={handleAgeChange} />
                <input type="text" placeholder="Company" value={company} onChange={handleCompanyChange} />
                <button type="submit">Register</button>
            </form>
            {submittedData && (
                <div>
                    <h2>Submitted Data</h2>
                    <p>Name: {submittedData.name}</p>
                    <p>Age: {submittedData.age}</p>
                    <p>Company: {submittedData.company}</p>
                </div>
            )}
        </div>
    );
};

export default Registration;
